yaml:
{
    "metadata": {
        "name": "requirements-writer-langgraph",
        "displayName": "Requirements Writer (LangGraph)",
        "description": "Transform ideas into professional requirements documents using LangGraph workflow",
        "version": "1.0.0",
        "type": "api",
        "department": "engineering"
    },
    "configuration": {
        "api": {
            "endpoint": "http://localhost:7200/workflows/requirements-writer",
            "method": "POST",
            "headers": {
                "Content-Type": "application/json"
            },
            "body": {
                "taskId": "{{taskId}}",
                "conversationId": "{{conversationId}}",
                "userId": "{{userId}}",
                "prompt": "{{userMessage}}",
                "provider": "{{llmSelection.providerName}}",
                "model": "{{llmSelection.modelName}}",
                "statusWebhook": "{{env.AGENT_BASE_URL}}:{{env.API_PORT}}/webhooks/status?taskId={{taskId}}",
                "metadata": "{{payload.metadata}}"
            },
            "authentication": {
                "type": "none"
            },
            "response_mapping": {
                "status_field": "success",
                "result_field": "data"
            },
            "timeout": 240000
        },
        "deliverable": {
            "format": "markdown",
            "type": "requirements-document"
        },
        "execution_modes": [
            "immediate",
            "polling",
            "real-time"
        ],
        "execution_capabilities": {
            "supports_converse": false,
            "supports_plan": false,
            "supports_build": true
        }
    }
}
