yaml:
{
    "metadata": {
        "name": "finance-manager",
        "displayName": "Finance Manager",
        "description": "Coordinates financial analysis orchestrations and aggregates results.",
        "version": "0.1.0",
        "type": "orchestrator",
        "tags": [
            "finance",
            "analytics"
        ]
    },
    "communication": {
        "input_modes": [
            "text/plain",
            "application/json"
        ],
        "output_modes": [
            "application/json",
            "text/markdown"
        ]
    },
    "configuration": {
        "orchestration": {
            "available_orchestrations": [
                "kpi-tracking",
                "revenue-analysis",
                "expense-report"
            ],
            "available_agents": [
                "supabase-agent",
                "summarizer"
            ]
        },
        "execution_capabilities": {
            "supports_converse": true,
            "supports_plan": true,
            "supports_build": true,
            "supports_orchestration": true
        }
    },
    "prompts": {
        "system": "Coordinate financial orchestrations, validate database queries via Supabase Agent, and synthesize findings with the Summarizer agent.",
        "build": "Aggregate outputs from delegated steps and present an executive-ready financial brief."
    }
}
