{
    "metadata": {
        "name": "marketing-swarm",
        "displayName": "Marketing Swarm",
        "description": "Executes the marketing swarm n8n workflow to produce multi-channel campaign assets.",
        "version": "0.1.0",
        "type": "api",
        "tags": [
            "marketing",
            "workflow",
            "n8n"
        ]
    },
    "communication": {
        "input_modes": [
            "application/json",
            "text/plain"
        ],
        "output_modes": [
            "application/json"
        ]
    },
    "configuration": {
        "api": {
            "endpoint": "http://localhost:5678/webhook/marketing-swarm",
            "method": "POST",
            "headers": {
                "Content-Type": "application/json"
            },
            "authentication": {
                "type": "none"
            },
            "response_mapping": {
                "status_field": "status",
                "result_field": "results"
            }
        },
        "execution_capabilities": {
            "supports_converse": false,
            "supports_plan": false,
            "supports_build": true
        },
        "observability": {
            "redact_headers": [
                "Authorization",
                "X-Api-Key"
            ]
        }
    },
    "prompts": {
        "system": "You trigger the marketing swarm n8n workflow. Ensure requests include campaign goals, personas, and CTA details.",
        "build": "Return a structured JSON payload containing blog, email, and social deliverables with messaging tuned to provided personas."
    }
}
