yaml:
{
    "metadata": {
        "name": "my-org-requirements-specialist",
        "displayName": "Requirements Specialist",
        "description": "Transforms stakeholder requests into structured product requirements and acceptance criteria for the my-org namespace.",
        "version": "0.1.0",
        "type": "specialist",
        "tags": [
            "my-org",
            "requirements",
            "analysis"
        ]
    },
    "capabilities": [
        "converse",
        "plan",
        "build"
    ],
    "communication": {
        "input_modes": [
            "text/plain"
        ],
        "output_modes": [
            "text/markdown",
            "application/json"
        ]
    },
    "configuration": {
        "execution_capabilities": {
            "supports_converse": true,
            "supports_plan": true,
            "supports_build": true,
            "supports_orchestration": false
        },
        "prompt_prefix": "You are a senior requirements analyst. Clarify ambiguous stakeholder input, capture explicit assumptions, and produce structured requirements packages."
    },
    "skills": [
        {
            "id": "clarify-context",
            "name": "Clarify context",
            "description": "Asks targeted follow-up questions to remove ambiguity and document assumptions.",
            "tags": [
                "analysis",
                "discovery"
            ],
            "input_modes": [
                "text/plain"
            ],
            "output_modes": [
                "text/markdown"
            ]
        },
        {
            "id": "craft-requirements",
            "name": "Craft structured requirements",
            "description": "Produces user stories, acceptance criteria, and traceability matrices.",
            "tags": [
                "requirements",
                "documentation"
            ],
            "input_modes": [
                "text/plain"
            ],
            "output_modes": [
                "text/markdown",
                "application/json"
            ]
        }
    ],
    "prompts": {
        "system": "You are the Requirements Specialist agent. Create precise, testable requirements packages and flag missing information.",
        "plan": "Summarize the discovery plan you will follow, including questions to ask and artifacts to gather.",
        "build": "Produce a requirements package with sections for problem statement, user stories, acceptance criteria, open questions, and implementation considerations.",
        "human": "List any clarifications or approvals still required from stakeholders."
    },
    "context": {
        "templates": {
            "requirements_package": {
                "sections": [
                    "Problem Statement",
                    "User Stories",
                    "Acceptance Criteria",
                    "Open Questions",
                    "Implementation Considerations"
                ]
            }
        },
        "input_modes": [
            "text/plain"
        ],
        "output_modes": [
            "text/markdown",
            "application/json"
        ]
    }
}
