{
    "metadata": {
        "name": "supabase-agent",
        "displayName": "Supabase Database Agent",
        "description": "Interacts with Supabase via MCP tools to introspect schema, generate SQL, and analyze results.",
        "version": "0.1.0",
        "type": "tool",
        "tags": [
            "database",
            "supabase",
            "analytics"
        ]
    },
    "communication": {
        "input_modes": [
            "text/plain",
            "application/json"
        ],
        "output_modes": [
            "application/json",
            "text/markdown"
        ]
    },
    "configuration": {
        "prompt_prefix": "You are a Supabase data specialist. Safely inspect schema, craft SQL, execute queries, and explain findings with clear business context.",
        "execution_capabilities": {
            "supports_converse": true,
            "supports_plan": false,
            "supports_build": true
        },
        "mcp": {
            "server": "supabase",
            "tools": [
                "get-schema",
                "generate-sql",
                "execute-sql",
                "analyze-results"
            ]
        },
        "security": {
            "allowed_tables": [
                "revenue",
                "expenses",
                "kpis",
                "metrics"
            ],
            "denied_operations": [
                "DROP",
                "TRUNCATE",
                "ALTER"
            ]
        }
    },
    "prompts": {
        "system": "You answer analytics questions about the Supabase warehouse. Validate assumptions, include SQL, and highlight anomalies.",
        "build": "Return an object with keys sql, rows, and summary. Use analyze-results to provide narrative insights for stakeholders.",
        "human": "List additional clarifications or approvals required before running potentially destructive operations."
    },
    "context": {
        "templates": {
            "analysis": {
                "sql": "SELECT ...",
                "summary": "Explain what the query returns and why it matters.",
                "next_steps": "Recommend follow-up investigations."
            }
        }
    }
}
