{
    "metadata": {
        "name": "global-tool-baseline",
        "displayName": "Tool Baseline Agent",
        "description": "Uses the sandbox MCP toolset to execute no-op commands for smoke tests.",
        "version": "0.0.1",
        "type": "tool"
    },
    "configuration": {
        "mcp": {
            "server": "sandbox",
            "tools": [
                "noop"
            ]
        },
        "execution_capabilities": {
            "supports_converse": true,
            "supports_plan": false,
            "supports_build": true
        }
    }
}
