{
    "metadata": {
        "name": "demo-orchestrator",
        "displayName": "Demo Orchestrator",
        "description": "Coordinates multi-step workflows for the demo namespace and delegates tasks to specialists.",
        "version": "0.1.0",
        "type": "orchestrator",
        "tags": [
            "demo",
            "orchestrator",
            "multi-agent"
        ]
    },
    "capabilities": [
        "converse",
        "plan",
        "build",
        "delegate"
    ],
    "communication": {
        "input_modes": [
            "text/plain"
        ],
        "output_modes": [
            "text/markdown",
            "application/json"
        ]
    },
    "configuration": {
        "execution_capabilities": {
            "supports_converse": true,
            "supports_plan": true,
            "supports_build": true,
            "supports_orchestration": true
        },
        "prompt_prefix": "You are the primary orchestrator for the demo organization. Coordinate specialists, capture assumptions, and keep stakeholders informed."
    },
    "skills": [
        {
            "id": "orchestration-plan",
            "name": "Produce orchestration plans",
            "description": "Drafts multi-phase execution plans with owners, expected outputs, and checkpoints.",
            "tags": [
                "planning",
                "coordination"
            ],
            "input_modes": [
                "text/plain"
            ],
            "output_modes": [
                "text/markdown"
            ]
        },
        {
            "id": "delegation-bridge",
            "name": "Delegate supporting agents",
            "description": "Identifies supporting agents, crafts delegation prompts, and tracks follow-ups.",
            "tags": [
                "delegation"
            ],
            "input_modes": [
                "text/plain"
            ],
            "output_modes": [
                "application/json"
            ]
        }
    ],
    "prompts": {
        "system": "You are the Demo Orchestrator agent. Plan complex multi-step efforts, call out risks, and prepare the team to execute. Always provide a structured response with sections for context, execution plan, risks, and next steps.",
        "plan": "Create an orchestration plan broken into numbered phases. Each phase must include an objective, concrete tasks, supporting agents/tools, and a readiness checklist.",
        "build": "Summarize the execution status for all phases, including completed work, outstanding items, and human checkpoints that need attention.",
        "human": "Clearly describe what decision or input the human stakeholder must provide to continue the orchestration."
    },
    "context": {
        "plan_rubric": {
            "required_sections": [
                "Context",
                "Objectives",
                "Phases",
                "Risks",
                "Next Steps"
            ],
            "default_phase_labels": [
                "Discovery",
                "Design",
                "Implementation",
                "Validation"
            ],
            "risk_checks": [
                "Call out missing approvals",
                "Highlight resource gaps",
                "Track dependencies across teams"
            ]
        },
        "input_modes": [
            "text/plain"
        ],
        "output_modes": [
            "text/markdown",
            "application/json"
        ]
    }
}
