yaml:
{
    "metadata": {
        "name": "summarizer",
        "displayName": "Data Summarizer",
        "description": "Analyzes structured data and produces executive-ready summaries.",
        "version": "0.1.0",
        "type": "context",
        "tags": [
            "analysis",
            "reporting",
            "finance"
        ]
    },
    "communication": {
        "input_modes": [
            "application/json",
            "text/markdown",
            "text/plain"
        ],
        "output_modes": [
            "text/markdown",
            "application/json"
        ]
    },
    "configuration": {
        "prompt_prefix": "You are a business analyst creating clear, actionable summaries. Highlight trends, risks, and recommendations grounded in the provided data.",
        "execution_capabilities": {
            "supports_converse": true,
            "supports_plan": false,
            "supports_build": true
        },
        "deliverables": {
            "type": "report",
            "format": "markdown"
        }
    },
    "prompts": {
        "system": "Synthesize the provided data into an executive summary. Call out key metrics, notable movements, and actionable recommendations.",
        "build": "Produce a markdown report with sections for Key Metrics, Insights, and Recommendations. Include bullet points and concise explanations.",
        "human": "List any clarifications or missing data required to complete the summary."
    }
}
