{
  "metadata": {
    "name": "supabase-agent",
    "displayName": "Supabase Tool Agent",
    "description": "Wraps Supabase MCP for schema, SQL generation, safe execution, and results analysis.",
    "version": "0.1.0",
    "type": "tool"
  },
  "communication": {
    "input_modes": [
      "text/plain",
      "application/json"
    ],
    "output_modes": [
      "application/json",
      "text/markdown"
    ]
  },
  "configuration": {
    "prompt_prefix": "You are a Supabase data specialist. Use MCP tools to safely inspect schema, craft SQL, execute read-only queries, and explain findings.",
    "execution_capabilities": {
      "supports_converse": false,
      "supports_plan": false,
      "supports_build": true
    },
    "mcp": {
      "server": "supabase"
    },
    "tools": [
      "supabase/get-schema",
      "supabase/generate-sql",
      "supabase/execute-sql",
      "supabase/analyze-results"
    ],
    "toolParams": {
      "supabase/generate-sql": {
        "query": "{{userMessage}}",
        "max_rows": 100
      },
      "supabase/execute-sql": {
        "max_rows": 1000
      },
      "supabase/analyze-results": {
        "analysis_prompt": "Summarize for stakeholders"
      }
    },
    "toolExecutionMode": "sequential",
    "stopOnError": true,
    "security": {
      "denied_operations": [
        "DROP",
        "TRUNCATE",
        "ALTER",
        "DELETE",
        "UPDATE"
      ],
      "allowed_tables": [
        "agent_conversations",
        "kpi_data",
        "kpi_metrics",
        "users"
      ]
    },
    "deliverable": {
      "format": "markdown",
      "type": "data-analysis"
    }
  },
  "context": {
    "input_modes": [
      "text/plain",
      "application/json"
    ],
    "output_modes": [
      "application/json",
      "text/markdown"
    ],
    "supported_modes": [
      "build"
    ]
  }
}
