module.exports = async (input, ctx) => {
  // Agent Builder Orchestrator implementation
  const { step, data, conversationState } = input;

  // Initialize state if first interaction
  const state = conversationState || {
    step: 'intent',
    agentConfig: {},
    validationResults: null
  };

  switch (step || state.step) {
    case 'intent':
      // Collect agent intent and type
      if (!data?.agentType || !data?.purpose) {
        return {
          ok: true,
          format: 'text/markdown',
          content: '## Welcome to Agent Builder\n\nLet\'s create a new agent! First, tell me:\n1. What type of agent? (function, context, api, orchestrator)\n2. What should this agent do?',
          state: { ...state, step: 'intent' },
          needsInput: true
        };
      }
      state.agentConfig.agent_type = data.agentType;
      state.agentConfig.purpose = data.purpose;
      state.step = 'basic_info';
      return {
        ok: true,
        format: 'text/markdown',
        content: `Great! A **${data.agentType}** agent for: ${data.purpose}\n\nNow provide:\n- Organization slug (or leave blank for global)\n- Agent slug (lowercase, underscores)\n- Display name\n- Description`,
        state,
        needsInput: true
      };

    case 'basic_info':
      if (!data?.slug || !data?.displayName) {
        return {
          ok: true,
          format: 'text/markdown',
          content: 'Please provide the required fields: slug and displayName',
          state,
          needsInput: true
        };
      }
      state.agentConfig.organization_slug = data.organizationSlug || null;
      state.agentConfig.slug = data.slug;
      state.agentConfig.display_name = data.displayName;
      state.agentConfig.description = data.description || null;
      state.step = 'io_contract';
      return {
        ok: true,
        format: 'text/markdown',
        content: `## IO Contract\n\nDefine input and output modes for **${data.displayName}**:\n\nInput modes (e.g., application/json, text/plain):\nOutput modes (e.g., text/markdown, application/json):`,
        state,
        needsInput: true
      };

    case 'io_contract':
      if (!data?.inputModes || !data?.outputModes) {
        return {
          ok: true,
          format: 'text/markdown',
          content: 'Please provide both inputModes and outputModes as arrays',
          state,
          needsInput: true
        };
      }
      const yaml = `input_modes: ${JSON.stringify(data.inputModes)}\noutput_modes: ${JSON.stringify(data.outputModes)}\n`;
      state.agentConfig.yaml = yaml;
      state.step = 'agent_config';

      // Different prompts based on agent type
      const type = state.agentConfig.agent_type;
      let configPrompt = '';
      if (type === 'function') {
        configPrompt = '## Function Configuration\n\nProvide:\n- timeout_ms (max 30000)\n- code (JavaScript module.exports function)';
      } else if (type === 'context') {
        configPrompt = '## Context Configuration\n\nProvide:\n- system prompt (instructions for the agent)';
      } else if (type === 'api') {
        configPrompt = '## API Configuration\n\nProvide:\n- endpoint URL\n- HTTP method\n- request/response transforms';
      }

      return {
        ok: true,
        format: 'text/markdown',
        content: configPrompt,
        state,
        needsInput: true
      };

    case 'agent_config':
      const agentType = state.agentConfig.agent_type;


      if (state.agentConfig.agent_type === 'function') {
        if (!data?.code || !data?.timeoutMs) {
          return {
            ok: true,
            format: 'text/markdown',
            content: 'Please provide both code and timeoutMs',
            state,
            needsInput: true
          };
        }
        state.agentConfig.config = {
          configuration: {
            function: {
              timeout_ms: data.timeoutMs,
              code: data.code
            }
          }
        };
      } else if (state.agentConfig.agent_type === 'context') {
        if (!data?.systemPrompt) {
          return {
            ok: true,
            format: 'text/markdown',
            content: 'Please provide a system prompt',
            state,
            needsInput: true
          };
        }
        state.agentConfig.context = {
          system: data.systemPrompt
        };
      } else if (state.agentConfig.agent_type === 'api') {
        if (!data?.endpoint || !data?.method) {
          return {
            ok: true,
            format: 'text/markdown',
            content: 'Please provide endpoint and method',
            state,
            needsInput: true
          };
        }
        state.agentConfig.config = {
          configuration: {
            api: {
              api_configuration: {
                endpoint: data.endpoint,
                method: data.method
              }
            }
          }
        };
      }

      state.step = 'validate';
      return {
        ok: true,
        format: 'text/markdown',
        content: '## Validation\n\nValidating your agent configuration...\n\nReady to validate. Proceed?',
        state,
        action: 'validate',
        needsInput: false
      };

    case 'validate':
      // In real implementation, this would call ctx.services.agentValidation
      // For now, return mock validation
      state.validationResults = {
        schemaValid: true,
        policyValid: true,
        dryRunPassed: state.agentConfig.agent_type === 'function',
        issues: []
      };
      state.step = 'review';
      return {
        ok: true,
        format: 'text/markdown',
        content: `## Validation Results\n\n✅ Schema validation passed\n✅ Policy checks passed\n${state.agentConfig.agent_type === 'function' ? '✅ Dry-run successful' : ''}\n\nReady to create **${state.agentConfig.display_name}**?`,
        state,
        needsInput: true
      };

    case 'review':
      if (data?.approved !== true) {
        return {
          ok: true,
          format: 'text/markdown',
          content: 'Please confirm approval to create the agent',
          state,
          needsInput: true
        };
      }
      state.step = 'create';
      return {
        ok: true,
        format: 'text/markdown',
        content: 'Creating agent via admin API...',
        state,
        action: 'create',
        needsInput: false
      };

    case 'create':
      // In real implementation, this would call ctx.services.adminApi.upsertAgent
      const agentId = 'mock-agent-id-' + Date.now();
      return {
        ok: true,
        format: 'application/json',
        content: {
          success: true,
          message: `Agent **${state.agentConfig.display_name}** created successfully!`,
          agentId,
          config: state.agentConfig
        },
        state: { ...state, step: 'complete', agentId }
      };

    default:
      return {
        ok: true,
        format: 'text/markdown',
        content: 'Unknown step. Please start over.',
        state: { step: 'intent', agentConfig: {} },
        needsInput: true
      };
  }
};
